mapper.select:
select c.id as categoryId, c.name as categoryName, b.id as boardId, b.name as boardName
from boards as b
         left join categories as c on b.category_id = c.id;

select count(*) from posts as p left join boards as b on p.board_id = b.id where b.name = #{board_name}
and author like concat('%', #{search.searchValue}, '%')
and subject like concat('%', #{search.searchValue}, '%')
and text like concat('%', #{search.searchValue}, '%') ;

select name
from boards
where id = #{boardId};
